let inputArr = grapher.getNodes().map(node => node.value);

let len = inputArr.length;

for (let i = 0; i < len; i++) {
	yield {
      line: 0,
      comment: (i < len) 
      					? i + " < " + len + ", so we stay in the loop"
    					: i + " is = to " + len + ", so we exit the loop"
    }
    grapher.setNodeModeSelected(i);

  	for (let j = 0; j < len - 1; j++) {
        yield {
          line: 1,
          comment: (j < len-1) 
      					? j + " < " + len + ", so we stay in the loop"
    					: j+ " is = to " + len + ", so we exit the loop"
        }

    	if (inputArr[j] > inputArr[j + 1]) {
      		yield {
				line: 2,
                comment: inputArr[j] + " > " + inputArr[j + 1]
            }

      		let tmp = inputArr[j];
      		yield 3

          	inputArr[j] = inputArr[j + 1];
			yield 4

          	inputArr[j + 1] = tmp;
            grapher.swap(j, j+1);
            yield 5
        }
    }
    grapher.setNodeModeNone(i);
}
